def get_decoded_features(proxy):
	decoded_features = []
	for x in proxy:
		if x in inv_mapping.keys():
			decoded_features.append(inv_mapping[x])
		else:
			decoded_features.append(inv_mapping[inv_binary_convertor[x]])
	return set(decoded_features)